<?php
/**
 * Controller is the customized base controller class.
 * All controller classes for this application should extend from this base class.
 */
class Controller extends CController
{
	/**
	 * @var string the default layout for the controller view. Defaults to '//layouts/column1',
	 * meaning using a single column layout. See 'protected/views/layouts/column1.php'.
	 */
	public $layout = '//layouts/main';
	/**
	 * @var array context menu items. This property will be assigned to {@link CMenu::items}.
	 */
	public $menu=array();
	/**
	 * @var array the breadcrumbs of the current page. The value of this property will
	 * be assigned to {@link CBreadcrumbs::links}. Please refer to {@link CBreadcrumbs::links}
	 * for more details on how to specify this property.
	 */
	public $breadcrumbs=array();

	public $menuTop=array();
	public $menuLeft=array();
	public $notices=array();
	public $nodeData=array();
	public $clans=array();
	public $site;
	public $redis;
	public $cluster = [];

	public function init()
	{
		$this->site = Sites::model()->cache(3600, new CExpressionDependency('Yii::app()->cache->get("sites")'))->with('style')->findByAttributes(array('url'=>Yii::app()->request->serverName));
		if (!$this->site)
		{
			exit('<!DOCTYPE html>
<html><head>	<meta charset="utf-8">	<meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1">	<meta name="viewport" content="width=device-width">	<title></title>    <style type="text/css">

@font-face{font-family:\'RussiaLight\';src:url(../font/russia-light-webfont.eot);src:url(../font/russia-light-webfont.woff) format("woff"),url(../font/russia-light-webfont.ttf) format("truetype");font-weight:400;font-style:normal}

*{margin:0;padding:0;box-sizing:border-box;-moz-box-sizing:border-box;-webkit-box-sizing:border-box}html{font-size:100%;-webkit-text-size-adjust:100%;-ms-text-size-adjust:100%}body{height:100%;background-image:url(data:image/jpeg;base64,);background-size:100% 100%;background-position:center center;background-attachment:fixed}.wotpw{width:100%;height:45%;margin:auto;position:absolute;top:0;left:0;bottom:0;right:0;text-align:center}.logo{margin:0 auto 38px;width:146px;height:163px;background-image:url(data:image/png;base64,)}h1{font-family:\'RussiaLight\',sans-serif;color:#fff;font-weight:400;margin-bottom:35px;font-size:2.4em}a{font-family:\'RussiaLight\',sans-serif;color:#fff;text-decoration:none;border:1px solid #fff;background-color:#424554;font-size:14px;padding:9px 38px 8px;-webkit-border-radius:2px;-moz-border-radius:2px;border-radius:2px;-webkit-transition:border .2s;-moz-transition:border .2s;transition:border .2s}a:hover{border:1px solid #424554}	</style></head><body><div class="wotpw">	<div class="logo"></div>	<h1>Добро пожаловать на сервер WoT.pw</h1>	<a href="http://wot.pw/">ПЕРЕЙТИ НА САЙТ</a></div></body></html>');
		}
		if ($this->site->reset)
			$this->site->reset();

		if ($this->site->banned)
		{
			throw new CHttpException(423,'Сайт заблокирован.');
		}

		if (Yii::app()->user->account && Yii::app()->user->account->banned)
		{
			throw new CHttpException(403,'Доступ запрещен.');
		}

		if ($this->site->url == 'devsite.wot.pw' && !isset($_COOKIE['dev']))
		{
			exit('Доступ запрещен.');
		}

		$this->redis = new Redis;
		if (!$this->redis->connect('127.0.0.1'))
			exit('Невозможно подключиться к серверу Redis');
		$this->redis->select(1);

		$this->setCluster($this->site->cluster);

		Yii::app()->theme = $this->site->style->theme;
		Yii::app()->language = $this->site->language;

		$this->pageTitle = Config::all('site_name')->value;

		if (substr(Yii::app()->request->requestUri, 0 , 6) == '/admin')
			$this->layout = '//layouts/admin';

		$this->clans = Clans::model()->cache(3600, new CExpressionDependency('Yii::app()->cache->get("clans")'))->findAll(array('index'=>'id', 'order'=>'`order`'));

		$menuTop = MenuTop::model()->cache(3600, new CExpressionDependency('Yii::app()->cache->get("menuTop")'))->findAll(array('order'=>'`order`'));
		foreach ($menuTop as $key=>$value)
		{
			$this->menuTop[$key] = array(
				'label'=>$value->label,
				'url'=>trim($value->url),
			);
			if (trim($value->highlight))
			{
				foreach (explode("\n", $value->highlight) as $sub)
				{
					if (trim($sub))
					{
						$data = explode(' ', trim($sub), 2);
						$this->menuTop[$key]['items'][] = array(
							'label'=>empty($data[1]) ? 'Submenu Item' : $data[1],
							'url'=>$data[0],
						);
					}
				}
			}
		}

		$menuLeft = MenuLeft::model()->cache(3600, new CExpressionDependency('Yii::app()->cache->get("menuLeft")'))->findAll(array('order'=>'`order`'));
		foreach ($menuLeft as $key=>$value)
		{
			$this->menuLeft[$key] = array(
				'label'=>$value->label,
				'url'=>trim($value->url),
			);
			if (trim($value->highlight))
			{
				foreach (explode("\n", $value->highlight) as $sub)
				{
					if (trim($sub))
					{
						$data = explode(' ', trim($sub), 2);
						$this->menuLeft[$key]['items'][] = array(
							'label'=>empty($data[1]) ? 'Submenu Item' : $data[1],
							'url'=>$data[0],
						);
					}
				}
			}
		}

		if (!Yii::app()->user->isGuest)
		{
			//Обновление актуальности сайта
			if ($this->site->check_life_time < time() - 3600)
			{
				$this->site->check_life_time = time();
				$this->site->update(['check_life_time']);
			}

			Yii::app()->params['skipSiteCheck'] = true;
			$noticesRecipients = NoticesRecipients::model()->with('notice')->findAll("`t`.`account_id` = '".Yii::app()->user->id."' AND `t`.`answer` = '0'");
			foreach ($noticesRecipients as $value)
			{
				$this->notices[] = array('id'=>$value->id, 'notice'=>Yii::app()->format->html($value->notice->notice), 'answers'=>explode(',', $value->notice->answers));
			}
			Yii::app()->params['skipSiteCheck'] = false;
		}

		if (empty(Yii::app()->request->cookies['clientId']->value))
		{
			$clientId = md5(rand().microtime());
			Yii::app()->request->cookies['clientId'] = new CHttpCookie('clientId', $clientId, ['expire'=>time()+3600*24*365]);
		}
		else
			$clientId = Yii::app()->request->cookies['clientId']->value;

		$this->nodeData = [[
			'account_id'=>Yii::app()->user->account ? (int)Yii::app()->user->account->id : 0,
			'chat_write'=>Yii::app()->user->account ? 1 : 0,
			'clan_id'=>Yii::app()->user->account ? (int)Yii::app()->user->account->clan_id : 0,
			'clan_on_site'=>Yii::app()->user->account ? ((int)isset($this->clans[Yii::app()->user->account->clan_id])) : 0,
			'client_id'=>$clientId,
			'language'=>$this->site->language,
			'nickname'=>Yii::app()->user->account ? Yii::app()->user->account->nickname : '',
			'avatar'=>Yii::app()->user->account ? Yii::app()->user->account->getAvatar() : '',
			'pmsg'=>Yii::app()->user->account && in_array(15, $this->site->premium_widgets) ? 1 : 0,
			'role'=>Yii::app()->user->account ? Yii::app()->user->account->role : '',
			'site_id'=>(int)$this->site->id,
		]];
		$token = md5(implode('', $this->nodeData[0]).'D=%b(1rb2RPKGDr-=)');
		$this->nodeData[1] = $token;

		if (isset($_GET['token']) && preg_match('=^\w{32}$=', $_GET['token']))
		{
			Yii::app()->user->setState('token', $_GET['token']);
		}
	}

	public function filters()
	{
		return array(
			'accessControl',
		);
	}

	public function accessRules()
	{
		$rule = 'allow';
		$message = '';
		$access = Access::model()->findAll(array('order'=>"LENGTH(`url`)"));
		foreach ($access as $value)
		{
			if (strpos(Yii::app()->request->requestUri, $value->url) === 0)
			{
				$rule = 'deny';
				$message = '<p><b>Доступ к странице ограничен.</b></p><p>Доступ имеют: '.implode(', ', array_intersect_key($value->rolesAllowed, array_flip(explode(',', $value->roles)))).' следующих кланов: '.implode(', ', array_intersect_key(CHtml::listData(Clans::model()->findAll(), 'id', 'abbreviation'), array_flip(explode(',', $value->clans)))).'</p>';
				if (Yii::app()->user->account && Yii::app()->user->account->clan_id)
				{
					$clans = explode(',', $value->clans);
					$roles = explode(',', $value->roles);
					if (in_array(Yii::app()->user->account->clan_id, $clans) && in_array(Yii::app()->user->account->role, $roles))
						$rule = 'allow';
				}
			}
		}

		return array(
			array('allow',
				'roles'=>array('0','1'),
			),
			array($rule,
				'users'=>array('*'),
				'controllers'=>array('forum', 'news', 'page', 'profile', 'recruitment', 'staff', 'custom'),
				'message'=>$message,
			),
		);
	}

	public function setCluster($cluster)
	{
		$this->cluster = Yii::app()->params['clusters'][$cluster];
	}

	public function jsonResponse($data)
	{
		header('Content-Type: application/json');
		echo json_encode($data, JSON_UNESCAPED_UNICODE | JSON_UNESCAPED_SLASHES);
		Yii::app()->end();
	}
}
